mapper.select:
select rno, order_no,order_gubun,order_count,order_startdate,order_indate,order_unitprice,order_enddate ,ivgroup_code, ivgroup_name, ivgroup_gubun ,ivgroup_size,totalprice,empno_r ,order_writer,delivery_state, order_memo from(select rownum rno, order_no,order_gubun,order_count,order_startdate,order_indate,order_unitprice,order_enddate ,ivgroup_code, ivgroup_name, ivgroup_gubun ,ivgroup_size,totalprice,empno_r ,order_writer,delivery_state, order_memo from(select order_no,order_gubun,order_count,order_startdate,order_indate,order_unitprice,order_enddate ,ivg.ivgroup_code ivgroup_code,ivg.ivgroup_name ivgroup_name,ivg.ivgroup_gubun ivgroup_gubun ,ivg.ivgroup_size ivgroup_size,order_unitprice*order_count totalprice,orde.order_writer empno_r ,emp.emp_name order_writer,delivery_state, order_memo from order_deli orde,invengroup ivg,emp where orde.ivgroup_code=ivg.ivgroup_code and orde.order_writer = emp.empno and order_gubun in('구매','사용') order by order_no desc) a where 1=1
and order_gubun = '구매'
and order_gubun = '사용'
and ivgroup_name like '%'||#{s_keyword}||'%'
and ivgroup_code like '%'||#{s_keyword}||'%'
and order_writer like '%'||#{s_keyword}||'%'
and order_indate between #{s_start_date_ud} and #{s_end_date_ud}
and order_indate between #{s_start_date_ud} and (select max(order_indate) from order_deli)
and order_indate between (select min(order_indate) from order_deli) and #{s_end_date_ud}
) b where 1=1
and rno between #{start} and #{end}

mapper.insert:
{call proc_invenadd(#{order_count,mode=IN, jdbcType=NUMERIC, javaType=java.lang.Integer}
  				   ,#{order_enddate,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
  				   ,#{order_unitprice,mode=IN, jdbcType=NUMERIC, javaType=java.lang.Integer}
  				   ,#{order_startdate,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
  				   ,#{ivgroup_code,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
  				   ,#{order_gubun,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
  				   ,#{s_emp_no,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
  				   ,#{order_memo,mode=IN, jdbcType=VARCHAR, javaType=java.lang.String})}

